var buttonsBuy=document.querySelectorAll(".button-buy"),popupcart=document.querySelector(".modal-cart-add"),closeAddCart=popupcart.querySelectorAll(".modal-add-close"),userBlock=document.querySelector(".user-navigation"),login=userBlock.querySelector(".login"),logout=userBlock.querySelector(".logout");login.addEventListener("click",function(e){e.preventDefault(),userBlock.classList.add("auth")}),logout.addEventListener("click",function(e){e.preventDefault(),userBlock.classList.remove("auth")});for(var i=0;i<buttonsBuy.length;i++){var buttonBuy=buttonsBuy[i];buttonBuy.addEventListener("click",function(e){e.preventDefault(),popupcart.classList.add("modal-show")})}for(i=0;i<closeAddCart.length;i++){var buttonClose=closeAddCart[i];buttonClose.addEventListener("click",function(e){e.preventDefault(),popupcart.classList.remove("modal-show")})}window.addEventListener("keydown",function(e){if(27===e.keyCode){e.preventDefault();for(var t=document.querySelectorAll(".modal-show"),o=0;o<t.length;o++)t[0].classList.remove("modal-show")}});